package com.Entity;
//车型类
public class Arctic {

	private int arctic_id;        //车型id
	private String arctic_name;   //车型名称
	private Brand brand; 		  //品牌
	
	private String cre_time;      //创建日期

	public Arctic(String string) {
		// TODO Auto-generated constructor stub
		this.arctic_name=string;
		
	}

	public int getArctic_id() {
		return arctic_id;
	}

	public void setArctic_id(int arctic_id) {
		this.arctic_id = arctic_id;
	}

	public String getArctic_name() {
		return arctic_name;
	}

	public void setArctic_name(String arctic_name) {
		this.arctic_name = arctic_name;
	}

	public Brand getBrand() {
		return brand;
	}

	public void setBrand(Brand brand) {
		this.brand = brand;
	}

	public String getCre_time() {
		return cre_time;
	}

	public void setCre_time(String cre_time) {
		this.cre_time = cre_time;
	}

	@Override
	public String toString() {
		return "Arctic [arctic_id=" + arctic_id + ", arctic_name=" + arctic_name + ", brand=" + brand + ", cre_time="
				+ cre_time + "]";
	}
	
	
}
